import './App.css';
import { useState } from 'react';

function App() {
  const[age,setAge]=useState(0);

  const increaseAge=()=>{
    setAge(1)
  }

  return( <div className ="App">
    {age} <button onClick={increaseAge}>Increase Age</button>

  </div>
  )
}



export default App;
